	function __construct($no)
    {
		if(substr($no, 0, 1) == '+'){
			$no = str_replace('+62', '0', $no);
		}else if(substr($no, 0, 1) == '6'){
			$no = str_replace('62', '0', $no);
		}
		$this->no = $no;
    }
	
	function lookup()
    {
		$result = array();
		if(!empty($this->telkomsel())){
			$result['provider'] = 'Telkomsel';
			$result['city'] = $this->telkomsel();
		}else if(!empty($this->indosat())){
			$result['provider'] = 'Indosat';
			$result['city'] = $this->indosat();
		}else if(!empty($this->xl())){
			$result['provider'] = 'XL';
			$result['city'] = $this->xl();
		}
		return $result;
	}
    
